!function(){var e=new Date,c=e.getDay(),t=e.getHours(),n=document.getElementById("active__dot");c>0&&c<6&&t>8&&t<17&&n.classList.add("online");var o=document.documentElement;document.querySelector(".open__off-canvas").onclick=function(){o.classList.add("active")},document.querySelector(".close__off-canvas").onclick=function(){o.classList.remove("active")},o.onclick=function(e){e.target===o&&o.classList.remove("active")}}();